        static int[] cutTheSticks(int[] arr)
        {
            List<int> res = new List<int>();
            List<int> com = new List<int>(arr);
            int min = 0;
            int lastCount = 0;
            int nextMin = com[0];
            bool allEq = true;

            for (int i = 0; i < (com.Count - 1); i++)
            {
                if (arr[i] < nextMin)
                {
                    nextMin = arr[i];
                }

                for (int j = i + 1; j < com.Count && allEq; j++)
                {
                    if (com[i] != com[j])
                    {
                        allEq = false;
                        break;
                    }
                }
            }

            res.Add(com.Count);

            while (com.Count > 1 && !allEq)
            {
                min = nextMin;
                for (int i = 0; i < com.Count; i++)
                {
                    com[i] = com[i] - min;

                    if (com[i] < nextMin && com[i] != 0)
                    {
                        nextMin = com[i];
                    }
                }

                com = com.Where((item, index) => item != 0).ToList();

                allEq = true;
                for (int i = 0; i < (com.Count - 1) && allEq; i++)
                {
                    for (int j = i + 1; j < com.Count && allEq; j++)
                    {
                        if (arr[i] != arr[j])
                        {
                            allEq = false;
                            break;
                        }
                    }
                }

                if (allEq)
                {
                    if (com.Count == 0)
                    {
                        return res.ToArray();
                    }
                    res.Add(com.Count);
                    com.RemoveAll(item => item >= 0);
                    lastCount = com.Count;
                }

                if (lastCount != com.Count)
                {
                    lastCount = com.Count;
                    res.Add(com.Count);
                }
            }

            return res.ToArray();
        }
